import http from '../http'
import store from '../store'

// 获取数据列表
export function fetchDataList(params) {
  return http({
    url: store.state.url.datum.data,
    method: 'get',
    params
  })
}

// 获取数据详情
export function fetchDataDetail(id) {
  return http({
    url: store.state.url.datum.dataDetail.replace('{id}', id),
    method: 'get'
  })
}