<template>
  <div class="data-container">
    <div class="filter-container">
      <el-input
        v-model="listQuery.keyword"
        placeholder="请输入关键词"
        style="width: 200px;"
        class="filter-item"
        @keyup.enter.native="handleFilter"
      />
      <el-button class="filter-item" type="primary" icon="el-icon-search" @click="handleFilter">
        搜索
      </el-button>
    </div>
    
    <el-table
      v-loading="loading"
      :data="dataList"
      border
      fit
      highlight-current-row
      style="width: 100%;"
    >
      <!-- 根据您的数据结构定义表格列 -->
      <el-table-column label="ID" prop="id" align="center" width="80" />
      <el-table-column label="名称" prop="name" align="center" />
      <el-table-column label="描述" prop="description" align="center" />
      <el-table-column label="创建时间" prop="created_at" align="center" width="180">
        <template slot-scope="{row}">
          <span>{{ row.created_at | formatDate }}</span>
        </template>
      </el-table-column>
      
      <el-table-column label="操作" align="center" width="180" class-name="small-padding fixed-width">
        <template slot-scope="{row}">
          <el-button type="primary" size="mini" @click="handleDetail(row)">
            查看
          </el-button>
        </template>
      </el-table-column>
    </el-table>
    
    <pagination
      v-show="total > 0"
      :total="total"
      :page.sync="listQuery.page"
      :limit.sync="listQuery.limit"
      @pagination="getList"
    />
  </div>
</template>

<script>
import { mapState, mapActions } from 'vuex'
import Pagination from '@/components/Pagination' // 假设您有一个分页组件

export default {
  name: 'DataList',
  components: { Pagination },
  filters: {
    formatDate(time) {
      // 实现日期格式化逻辑
      return new Date(time).toLocaleString()
    }
  },
  data() {
    return {
      listQuery: {
        page: 1,
        limit: 10,
        keyword: ''
      }
    }
  },
  computed: {
    ...mapState('data', ['dataList', 'loading', 'total'])
  },
  created() {
    this.getList()
  },
  methods: {
    ...mapActions('data', { fetchDataList: 'getDataList' }),
    
    getList() {
      this.fetchDataList(this.listQuery)
    },
    
    handleFilter() {
      this.listQuery.page = 1
      this.getList()
    },
    
    handleDetail(row) {
      this.$router.push(`/data/detail/${row.id}`)
    }
  }
}
</script>

<style scoped>
.data-container {
  padding: 20px;
}
.filter-container {
  padding-bottom: 10px;
}
.filter-item {
  margin-bottom: 10px;
  margin-right: 10px;
}
</style>